import React, { Component } from 'react';
import './App.css';
import TransactionTable from './views/TransactionTable/TransactionTable.js';
import ReconnectingWebSocket from 'reconnecting-websocket'
const axios = require('axios');

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      messageValue: "",
      address: "",
      balance: 0,
      transactions: [],
      connected: false,
      ping: ""
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleKeyPress = this.handleKeyPress.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  componentDidMount() {
      function satToBtc(x) {
          return x * 0.00000001;
      };

      const socket = new WebSocket('wss://ws.blockchain.info/inv');
      socket.debug = true;
      socket.timeoutInterval = 3000;
      const address = this.state.address;

      if(address !== "") {
        this.setState({ping: {"op":"addr_sub", "addr":address}});
      }
      else {
        this.setState({ping: {"op":"unconfirmed_sub"}});
      }

      socket.addEventListener('open', function (event) {
         const ping = JSON.stringify(this.state.ping)
         socket.send(ping)
         this.setState({connected:true})
      }.bind(this));

      socket.addEventListener('close', function(event) {
          this.setState({connected:false})
      }.bind(this));

      socket.addEventListener('error', function(event) {
          this.setState({connected:false})
      }.bind(this));

      socket.addEventListener('message', function(event) {
        const data = JSON.parse(event.data)
        const transactions = this.state.transactions

        var newTransaction = {
          hash: data.hash,
          amount: satToBtc(data.size).toFixed(8),
        }

        transactions.push(newTransaction)
        this.setState({transactions: transactions})
      }.bind(this));
   }

  handleChange(event) {
    this.setState({messageValue: event.target.value});
  }

  handleKeyPress(event) {
    var code = event.keyCode || event.which;
    if(code === 13 && code !== 16) {
        this.handleSubmit(event);
        this.setState({messageValue: ""});
        event.preventDefault();
    }
  }

