import React, { Component } from 'react';
import './App.css';
import TransactionTable from './views/TransactionTable/TransactionTable.js';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      messageValue: "",
      hashValue: ""
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleKeyPress = this.handleKeyPress.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    this.setState({messageValue: event.target.value});
  }

  handleKeyPress(event) {
    var code = event.keyCode || event.which;
    if(code === 13 && code !== 16) {
        this.handleSubmit(event);
        event.preventDefault();
    }
  }

  handleSubmit(event) {
    const messageValue = this.state.messageValue;
    this.setState({hashValue: messageValue});
    this.setState({messageValue: ""});
    event.preventDefault();
  }

  render() {
    return (
      <div>
        <div className="animated fadeInDown">
          <div className="header">
            <div className="bitcoinlogo">
              <img src={require('./icons/goldbitcoinicon.png')} />
            </div>
            <div className="title">BitcoinTrans</div>
          </div>
        </div>

        <div className="animated fadeInUp">
          <div className="transactions">
            <div className="animated fadeInLeft">
              <div className="transaction-header">
                <p>Transactions</p>
                <textarea
                  placeholder="Search by BTC address..."
                  value={this.state.messageValue}
                  onKeyPress={this.handleKeyPress}
                  onChange={this.handleChange}
                  >
                </textarea>
              </div>
            </div>
            <div id="transactiontable"> <TransactionTable hashValue={this.state.hashValue}/></div>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
