import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import './TransactionTable.css'
import TransTableRow from '../../components/TransTableRow/TransTableRow.js';
const axios = require('axios');

class TransactionTable extends Component {
  constructor(props) {
    super(props);
    this.state = {
      address: "",
      transactions: [],
      hash: "",
      btc: "",
      usd: ""
    };
  }

  componentDidMount() {
    axios.get('https://blockchain.info/rawaddr/1dice8EMZmqKvrGE4Qc9bUFf9PX3xaYDp')
      .then(function (response) {
        // handle success
        console.log(response);
        this.setState({address: response.data.address})
      })
      .catch(function (error) {
        // handle error
        console.log(error);
      }).bind(this)
  }

  render() {
    return (
      <div className="trans-table">
        <div className="table-header">
          <div id="trans-hash">Hash</div>
          <div id="amt-btc">Transaction (BTC)</div>
          <div id="amt-usd">Transaction (USD)</div>
          <div id="balance">Balance</div>
        </div>

        <div className="table-body">
          <TransTableRow />
        </div>
      </div>
    );
  }
}

export default TransactionTable;
