import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import './TransactionTable.css'
import TransTableRow from '../../components/TransTableRow/TransTableRow.js';
const axios = require('axios');

class TransactionTable extends Component {
  constructor(props) {
    super(props);
    this.state = {
      address: "",
      transactions: []
    };
  }

  componentDidMount() {
    axios.get('https://blockchain.info/rawaddr/1dice8EMZmqKvrGE4Qc9bUFf9PX3xaYDp')
      .then((response) => {
        // handle success
        console.log(response);
        this.setState({
          address: response.data.address,
        });

        let transactions = this.state.transactions;

        for(var i=0;i<response.data.txs.length; i++) {
          var newTransaction = {
            hash: response.data.txs[i].hash,
            totalSent:response.data.txs[i].total_sent,
            totalReceived: response.data.txs[i].total_received,
            balance: response.data.txs[i].final_balance
          }
          transactions.push(newTransaction);
          this.setState({transactions: transactions});
        }
      })
      .catch(function (error) {
        // handle error
        console.log(error);
      });
  }

  render() {
    return (
      <div className="trans-table">
        <div className="table-header">
          <div id="trans-hash">Hash</div>
          <div id="amt-btc">Amount (BTC)</div>
          <div id="total-sent">Total Sent</div>
          <div id="total-received">Total Received</div>
          <div id="balance">Balance</div>
        </div>

        <div className="table-body">
          <TransTableRow />
        </div>
      </div>
    );
  }
}

export default TransactionTable;
