import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import './TransactionTable.css'
import TransTableRow from '../../components/TransTableRow/TransTableRow.js';
const axios = require('axios');

class TransactionTable extends Component {
  constructor(props) {
    super(props);
    this.state = {
      address: this.props.hashValue,
      transactions: []
    };
  }

  componentWillMount() {
    axios.get('https://blockchain.info/rawaddr/$bitcoin_address',
      {
       params: {
         bitcoin_address: this.props.hashValue
       }
     })
      .then((response) => {
        // handle success
        console.log(response);
        this.setState({
          address: response.data.address,
        });

        let transactions = this.state.transactions;

        for(var i=0;i<response.data.txs.length; i++) {
          var newTransaction = {
            hash: response.data.txs[i].hash,
            amount:response.data.txs[i].size,
            balance: response.data.final_balance
          }
          transactions.push(newTransaction);
          this.setState({transactions: transactions});
        }
      })
      .catch(function (error) {
        // handle error
        console.log(error);
      });
  }

  render() {
    return (
      <div className="trans-table">
        <div className="table-header">
          <div id="trans-hash">Hash</div>
          <div id="amt-btc">Amount (BTC)</div>
          <div id="balance">Balance</div>
        </div>

        <div className="table-body">
          {this.state.transactions.map(transactions => (
            <TransTableRow hash={transactions.hash} amount={transactions.amount} balance={transactions.balance}/>
          ))}
        </div>
      </div>
    );
  }
}

export default TransactionTable;
